

/* ==========================================================================
   scrollUp
   ========================================================================== */
$(document).ready(function(){
    $("#toTop").hide();
    $(function () {
        $(window).scroll(function () {
            if ($(this).scrollTop() > 10) {
                $('#toTop').fadeIn();
            } else {
                $('#toTop').stop().fadeOut();
            }
        });
        $('#toTop').click(function () {
            $('body,html').animate({
                scrollTop: 0
            }, 800);
            return false;
        });
        
        $('#menu_video').click(function(){
            $('html,body').animate({scrollTop:$('#video').offset().top}, 800);
        });
        $('#menu_method').click(function(){
            $('html,body').animate({scrollTop:$('#method').offset().top}, 800);
        });
        $('#menu_travel').click(function(){
            $('html,body').animate({scrollTop:$('#travel').offset().top}, 800);
        });
		$('#menu_banenr').click(function(){
            $('html,body').animate({scrollTop:$('#banenr').offset().top}, 800);
        });
		$('#menu_gotop').click(function(){
            $('html,body').animate({scrollTop:$('#banner').offset().top}, 800);
        });
    });
})

